import React from "react";
import "./Contact.css";
const Contact = () => {
  class App extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        name: "",
        email: "",
        message: "",
      };
    }

    render() {
      return (
        <div className="App">
          <form
            id="contact-form"
            onSubmit={this.handleSubmit.bind(this)}
            method="POST"
          >
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                className="form-control"
                value={this.state.name}
                onChange={this.onNameChange.bind(this)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="exampleInputEmail1">Email address</label>
              <input
                type="email"
                className="form-control"
                aria-describedby="emailHelp"
                value={this.state.email}
                onChange={this.onEmailChange.bind(this)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="message">Message</label>
              <textarea
                className="form-control"
                rows="5"
                value={this.state.message}
                onChange={this.onMessageChange.bind(this)}
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
      );
    }

    onNameChange(event) {
      this.setState({ name: event.target.value });
    }

    onEmailChange(event) {
      this.setState({ email: event.target.value });
    }

    onMessageChange(event) {
      this.setState({ message: event.target.value });
    }

    handleSubmit(event) {}
  }
  return (
    <div className="container">
      <h1 className="contact-h1">Contact Us</h1>{" "}
      <p className="content">
        Thank you for stopping by Recipe Finder We would love to hear from you
        about any questions, comments or inquiries. You can email our team at
        Recipe@Finder.com.
      </p>
      <h2 className="subheading">Recipe Questions</h2>
      <p className="content">
        If you have questions or feedback about a recipe, please email us. We
        love hearing how recipes went and if you ever need help troubleshooting
        a recipe!
      </p>
      <form
        id="contact-form"
        onSubmit={this.handleSubmit.bind(this)}
        method="POST"
      >
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input type="text" className="form-control" />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputEmail1">Email address</label>
          <input
            type="email"
            className="form-control"
            aria-describedby="emailHelp"
          />
        </div>
        <div className="form-group">
          <label htmlFor="message">Message</label>
          <textarea className="form-control" rows="5"></textarea>
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
};
export default Contact;
