import React from "react";
import "./About.css";
const About = () => {
  return (
    <div className="container">
      <h1 className="about-h1">About</h1>
      <h2 className="subheading">About Recipe Finder</h2>
      <p className="content">
        Welcome! At Recipe Finder our number one goal is to make cooking easy!
        From fresh and easy recipes to ingredient guides and simple tutorials,
        it’s all here — all the tools, tips, tricks and techniques you need to
        get a great-tasting, good-for-you meal on the table every night. If
        you’re feeling drained by dinnertime decisions, you’ve come to the right
        place.
      </p>
    </div>
  );
};
export default About;
