import React from "react";
import "./Contact.css";
const Contact = () => {
  function printError(elemId, hintMsg) {
    document.getElementById(elemId).innerHTML = hintMsg;
  }

  document.querySelector("#contact-form").addEventListener("submit", (e) => {
    e.preventDefault();

    const name = document.contactForm.name.value;
    const email = document.contactForm.email.value;

    let nameErr = true;
    let emailErr = true;

    if (name === "") {
      printError("nameErr", "Please enter your name");
    } else {
      const regex = /^[a-zA-Z\s]+$/;
      if (regex.test(name) === false) {
        printError("nameErr", "Please enter a valid name");
      } else {
        printError("nameErr", "");
        nameErr = false;
      }
    }

    if (email === "") {
      printError("emailErr", "Please enter your email address");
    } else {
      // Regular expression for basic email validation
      const regex = /^\S+@\S+\.\S+$/;
      if (regex.test(email) === false) {
        printError("emailErr", "Please enter a valid email address");
      } else {
        printError("emailErr", "");
        emailErr = false;
      }
    }

    if ((nameErr || emailErr) === false) {
      alert("Thank you!");
    }
  });
  return (
    <div className="container">
      <h1 className="contact-h1">Contact Us</h1>{" "}
      <p className="content">
        Thank you for stopping by Recipe Finder We would love to hear from you
        about any questions, comments or inquiries. You can email our team at
        Recipe@Finder.com.
      </p>
      <h2 className="subheading">Recipe Questions</h2>
      <p className="content">
        If you have questions or feedback about a recipe, please email us. We
        love hearing how recipes went and if you ever need help troubleshooting
        a recipe!
      </p>
      <form
        id="contact-form"
        onSubmit={this.handleSubmit.bind(this)}
        method="POST"
      >
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input type="text" className="form-control" />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputEmail1">Email address</label>
          <input
            type="email"
            className="form-control"
            aria-describedby="emailHelp"
          />
        </div>
        <div className="form-group">
          <label htmlFor="message">Message</label>
          <textarea className="form-control" rows="5"></textarea>
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
};
export default Contact;
